func threeSum(nums []int) [][]int {
	results := [][]int{}
	var m map[[3]int]struct{}
	m = make(map[[3]int]struct{})

	sort.Ints(nums)
	for i := 0; i < len(nums); i++ {
		if nums[i] <= 0 {
			for j := i + 1; j < len(nums); j++ {
				if nums[i]+nums[j] <= 0 {
					for k := len(nums) - 1; k > j; k-- {
						if nums[i]+nums[j]+nums[k] == 0 {
							arr := [3]int{nums[i], nums[j], nums[k]}
							if _, hit := m[arr]; !hit {
								results = append(results, []int{nums[i], nums[j], nums[k]})
								m[arr] = struct{}{}
							}
						}
						if nums[i]+nums[j]+nums[k] <= 0 {
							break
						}
						for nums[k] == nums[k-1] {
							k--
						}
					}
				} else {
					break
				}
				for j+1 < len(nums) && nums[j] == nums[j+1] {
					j++
				}
			}
		} else {
			break
		}
		for i+1 < len(nums) && nums[i] == nums[i+1] {
			i++
		}
	}
	return results
}
